import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Box,
  Button,
  TextField,
  Typography,
  Paper,
  Alert
} from '@mui/material';

export default function LoginPage() {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // In a real app, you would call your authentication endpoint here
      // For demo purposes, we'll simulate different roles based on email
      let role = 'voter';
      if (credentials.email.includes('admin')) role = 'admin';
      
      // Store user info in localStorage (in a real app, use proper auth tokens)
      localStorage.setItem('user', JSON.stringify({
        email: credentials.email,
        role: role,
        uid: credentials.email // Using email as uid for demo
      }));
      
      // Redirect based on role
      if (role === 'admin') {
        navigate('/voters');
      } else {
        navigate('/votes');
      }
    } catch (error) {
      setError('Login failed. Please check your credentials.');
      console.error('Login error:', error);
    }
  };

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Paper sx={{ p: 4, width: 400 }}>
        <Typography variant="h5" gutterBottom>Login</Typography>
        
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        
        <TextField
          label="Email"
          fullWidth
          margin="normal"
          value={credentials.email}
          onChange={(e) => setCredentials({...credentials, email: e.target.value})}
        />
        
        <TextField
          label="Password"
          type="password"
          fullWidth
          margin="normal"
          value={credentials.password}
          onChange={(e) => setCredentials({...credentials, password: e.target.value})}
        />
        
        <Button 
          variant="contained" 
          fullWidth 
          sx={{ mt: 2 }}
          onClick={handleLogin}
        >
          Login
        </Button>
        
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Typography variant="body2">
            Demo accounts:
          </Typography>
          <Typography variant="body2">
            admin@example.com (admin)
          </Typography>
          <Typography variant="body2">
            voter@example.com (voter)
          </Typography>
        </Box>
      </Paper>
    </Box>
  );
}
