import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Box,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  Select,
  MenuItem,
  FormControl,
  InputLabel
} from '@mui/material';

export default function VotesPage() {
  const [votes, setVotes] = useState([]);
  const [elections, setElections] = useState([]);
  const [candidates, setCandidates] = useState([]);
  const [selectedElection, setSelectedElection] = useState('');
  const [selectedCandidate, setSelectedCandidate] = useState('');
  const [results, setResults] = useState([]);

  useEffect(() => {
    fetchElections();
    fetchCandidates();
  }, []);

  const fetchElections = async () => {
    try {
      const response = await axios.get('/elections');
      setElections(response.data);
    } catch (error) {
      console.error('Error fetching elections:', error);
    }
  };

  const fetchCandidates = async () => {
    try {
      const response = await axios.get('/candidates');
      setCandidates(response.data);
    } catch (error) {
      console.error('Error fetching candidates:', error);
    }
  };

  const fetchVotes = async () => {
    try {
      const response = await axios.get('/votes');
      setVotes(response.data);
    } catch (error) {
      console.error('Error fetching votes:', error);
    }
  };

  const fetchResults = async (electionId) => {
    try {
      const response = await axios.get(`/votes/results/${electionId}`);
      setResults(response.data);
    } catch (error) {
      console.error('Error fetching results:', error);
    }
  };

  const handleVote = async () => {
    try {
      await axios.post('/votes', {
        electionId: selectedElection,
        candidateId: selectedCandidate
      });
      alert('Vote submitted successfully!');
      fetchVotes();
      fetchResults(selectedElection);
    } catch (error) {
      console.error('Error submitting vote:', error);
      alert(error.response?.data?.error || 'Failed to submit vote');
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>Voting System</Typography>
      
      <Box sx={{ mb: 4 }}>
        <Typography variant="h6" gutterBottom>Cast Your Vote</Typography>
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Select Election</InputLabel>
          <Select
            value={selectedElection}
            onChange={(e) => setSelectedElection(e.target.value)}
          >
            {elections.map((election) => (
              <MenuItem key={election._id} value={election._id}>
                {election.name}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Select Candidate</InputLabel>
          <Select
            value={selectedCandidate}
            onChange={(e) => setSelectedCandidate(e.target.value)}
            disabled={!selectedElection}
          >
            {candidates.map((candidate) => (
              <MenuItem key={candidate._id} value={candidate._id}>
                {candidate.name} ({candidate.party})
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        
        <Button 
          variant="contained" 
          onClick={handleVote}
          disabled={!selectedElection || !selectedCandidate}
        >
          Submit Vote
        </Button>
      </Box>

      {selectedElection && (
        <Box sx={{ mb: 4 }}>
          <Typography variant="h6" gutterBottom>Election Results</Typography>
          <Button 
            variant="outlined" 
            onClick={() => fetchResults(selectedElection)}
            sx={{ mb: 2 }}
          >
            Refresh Results
          </Button>
          
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Candidate</TableCell>
                  <TableCell>Party</TableCell>
                  <TableCell>Votes</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {results.map((result) => (
                  <TableRow key={result.candidateId}>
                    <TableCell>{result.name}</TableCell>
                    <TableCell>{result.party}</TableCell>
                    <TableCell>{result.voteCount}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Box>
      )}

      <Box>
        <Typography variant="h6" gutterBottom>All Votes</Typography>
        <Button 
          variant="outlined" 
          onClick={fetchVotes}
          sx={{ mb: 2 }}
        >
          Refresh Votes
        </Button>
        
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Voter ID</TableCell>
                <TableCell>Candidate</TableCell>
                <TableCell>Election</TableCell>
                <TableCell>Timestamp</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {votes.map((vote) => {
                const election = elections.find(e => e._id === vote.electionId);
                const candidate = candidates.find(c => c._id === vote.candidateId);
                return (
                  <TableRow key={vote._id}>
                    <TableCell>{vote.voterId}</TableCell>
                    <TableCell>{candidate?.name || 'Unknown'}</TableCell>
                    <TableCell>{election?.name || 'Unknown'}</TableCell>
                    <TableCell>{new Date(vote.timestamp).toLocaleString()}</TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>
    </Box>
  );
}
