import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Box,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Alert,
  CircularProgress,
  Card,
  CardContent,
  Divider,
  Grid
} from '@mui/material';

export default function VotesPage() {
  const [votes, setVotes] = useState([]);
  const [elections, setElections] = useState([]);
  const [candidates, setCandidates] = useState([]);
  const [selectedElection, setSelectedElection] = useState('');
  const [selectedCandidate, setSelectedCandidate] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [user, setUser] = useState(null);
  
  // Get user info from localStorage
  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('user') || '{}');
    setUser(userData);
  }, []);

  useEffect(() => {
    fetchElections();
    fetchCandidates();
  }, []);
  
  // Fetch results when an election is selected
  useEffect(() => {
    if (selectedElection) {
      fetchResults(selectedElection);
    }
  }, [selectedElection]);

  const fetchElections = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await axios.get('/elections');
      setElections(response.data.data || []);
    } catch (error) {
      console.error('Error fetching elections:', error);
      setError('Failed to load elections. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const fetchCandidates = async () => {
    setLoading(true);
    try {
      const response = await axios.get('/candidates');
      setCandidates(response.data.data || []);
    } catch (error) {
      console.error('Error fetching candidates:', error);
      setError('Failed to load candidates. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const fetchVotes = async () => {
    setLoading(true);
    try {
      const response = await axios.get('/votes');
      setVotes(response.data.data || []);
    } catch (error) {
      console.error('Error fetching votes:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchResults = async (electionId) => {
    if (!electionId) return;
    
    setLoading(true);
    setError('');
    try {
      const response = await axios.get(`/votes/results/${electionId}`);
      setResults(response.data.data || []);
    } catch (error) {
      console.error('Error fetching results:', error);
      setError('Failed to load election results. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleVote = async () => {
    if (!selectedElection || !selectedCandidate) {
      setError('Please select both an election and a candidate');
      return;
    }
    
    setLoading(true);
    setError('');
    try {
      await axios.post('/votes', {
        electionId: selectedElection,
        candidateId: selectedCandidate,
        voterId: user.uid // Using user ID from localStorage
      });
      
      setSuccess('Your vote has been submitted successfully!');
      setSelectedCandidate(''); // Reset selection
      
      // Clear success message after 3 seconds
      setTimeout(() => setSuccess(''), 3000);
      
      // Refresh data
      fetchResults(selectedElection);
    } catch (error) {
      console.error('Error submitting vote:', error);
      setError(error.response?.data?.error || 'Failed to submit vote. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>Voting System</Typography>
      
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
      
      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 2 }}>
          <CircularProgress />
        </Box>
      )}
      
      {/* Only show voting form for voters */}
      {user?.role === 'voter' && (
        <Card sx={{ mb: 4 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>Cast Your Vote</Typography>
            <FormControl fullWidth sx={{ mb: 2 }}>
              <InputLabel>Select Election</InputLabel>
              <Select
                value={selectedElection}
                onChange={(e) => setSelectedElection(e.target.value)}
              >
                {elections.map((election) => (
                  <MenuItem key={election._id} value={election._id}>
                    {election.name}
                  </MenuItem>
                ))}
          </Select>
        </FormControl>
        
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Select Candidate</InputLabel>
          <Select
            value={selectedCandidate}
            onChange={(e) => setSelectedCandidate(e.target.value)}
            disabled={!selectedElection}
          >
            {candidates.map((candidate) => (
              <MenuItem key={candidate._id} value={candidate._id}>
                {candidate.name} ({candidate.party})
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        
        <Button 
          variant="contained" 
          color="primary" 
          onClick={handleVote}
          disabled={!selectedElection || !selectedCandidate || loading}
          fullWidth
        >
          {loading ? <CircularProgress size={24} color="inherit" /> : 'Submit Vote'}
        </Button>
      </CardContent>
    </Card>
    )}

      {selectedElection && (
        /* Election Results Section */
        <Card>
        <CardContent>
          <Typography variant="h6" gutterBottom>Election Results</Typography>
          
          {selectedElection ? (
            <>
              {results.length > 0 ? (
                <>
                  <Grid container spacing={2} sx={{ mb: 2 }}>
                    {results.map((result) => (
                      <Grid item xs={12} sm={6} md={4} key={result.candidateId}>
                        <Card variant="outlined">
                          <CardContent>
                            <Typography variant="h6">{result.name}</Typography>
                            <Typography variant="body2" color="textSecondary">
                              {result.party}
                            </Typography>
                            <Typography variant="h5" sx={{ mt: 2, color: 'primary.main' }}>
                              {result.voteCount} votes
                            </Typography>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                  
                  <Divider sx={{ my: 2 }} />
                  
                  <TableContainer component={Paper}>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Candidate</TableCell>
                          <TableCell>Party</TableCell>
                          <TableCell align="right">Votes</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {results.map((result) => (
                          <TableRow key={result.candidateId}>
                            <TableCell>{result.name}</TableCell>
                            <TableCell>{result.party}</TableCell>
                            <TableCell align="right">{result.voteCount}</TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </>
              ) : (
                <Typography>No votes have been cast for this election yet.</Typography>
              )}
            </>
          ) : (
            <Typography>Please select an election to view results.</Typography>
          )}
        </CardContent>
      </Card>
      )}

      <Box>
        <Typography variant="h6" gutterBottom>All Votes</Typography>
        <Button 
          variant="outlined" 
          onClick={fetchVotes}
          sx={{ mb: 2 }}
        >
          Refresh Votes
        </Button>
        
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Voter ID</TableCell>
                <TableCell>Candidate</TableCell>
                <TableCell>Election</TableCell>
                <TableCell>Timestamp</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {votes.map((vote) => {
                const election = elections.find(e => e._id === vote.electionId);
                const candidate = candidates.find(c => c._id === vote.candidateId);
                return (
                  <TableRow key={vote._id}>
                    <TableCell>{vote.voterId}</TableCell>
                    <TableCell>{candidate?.name || 'Unknown'}</TableCell>
                    <TableCell>{election?.name || 'Unknown'}</TableCell>
                    <TableCell>{new Date(vote.timestamp).toLocaleString()}</TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>
    </Box>
  );
}
